    private void jbInserirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbInserirActionPerformed
        // TODO add your handling code here:
        Mesa mesa = new Mesa();
        MesaDao mesaDao = new MesaDao();
        
        mesa.getFun().setIdFuncionario(Integer.parseInt(JT_FUNCIONARIO_IDFUNCIONARIO.getText()));
        
        try {
            mesaDao.Inserir(mesa);
        } catch (SQLException ex) {
            Logger.getLogger(jfCadastroMesa.class.getName()).log(Level.SEVERE, null, ex);
        }
        
    }//GEN-LAST:event_jbInserirActionPerformed

    private void jbExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbExcluirActionPerformed
        // TODO add your handling code here:
        Mesa mesa = new Mesa();
        MesaDao mesaDao = new MesaDao();
        
        mesa.setIdMesa(Integer.parseInt(JT_ID_MESA.getText()));
        
        try {
            mesaDao.remove(mesa);
        } catch (SQLException ex) {
            Logger.getLogger(jfCadastroMesa.class.getName()).log(Level.SEVERE, null, ex);
        }
        
    }//GEN-LAST:event_jbExcluirActionPerformed


